import React from "react";
import { useState } from "react";
import Card from "../UI/Card";
import classes from './AddUser.module.css';
import Button from "../UI/Button";


const AddUser = (props) => {

    const [enteredUserName, UpdatedUserName] = useState('');
    const [enteredAge, updatedAge] = useState('');

  
    const userNameHandler = (event) => {
        UpdatedUserName(event.target.value)
    }

    const ageHandler = (event) =>{
        updatedAge(event.target.value)
    }
    const addUserHandler =(event) => {
        event.preventDefault();
       console.log(enteredUserName,enteredAge);
       UpdatedUserName('');
       updatedAge(event.target.value)
    }
 
    return(
        <Card className={classes.input}>
            <form onSubmit={addUserHandler}>
            <label htmlFor="username">UserName</label>
            <input id="username" type="text" onChange={userNameHandler}></input>

            <label htmlFor="age">Age</label>
            <input id="age" type="number" onChange={ageHandler}></input>

            <Button type="submit">Submit</Button>

            </form>
        </Card>
    );
}

export default AddUser;
